mapper.update:
UPDATE TBL_COUNSELOR
SET COUNSELOR_TITLE = #{counselorTitle}, COUNSELOR_CONTENT = #{counselorContent}
WHERE COUNSELOR_NUMBER = #{counselorNumber}

mapper.select:
SELECT RNUM, COUNSELOR_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, USER_NUMBER, USER_NAME, USER_LEVEL, C_FILE_ROUTE, C_FILE_UUID, C_FILE_NAME FROM ( SELECT ROWNUM AS RNUM, COUNSELOR_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, USER_NUMBER, USER_NAME, USER_LEVEL, C_FILE_ROUTE, C_FILE_UUID, C_FILE_NAME FROM ( SELECT C.COUNSELOR_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, C.USER_NUMBER, U.USER_NAME, U.USER_LEVEL, C_FILE_ROUTE, C_FILE_UUID, C_FILE_NAME FROM TBL_COUNSELOR C JOIN TBL_USER U ON C.USER_NUMBER = U.USER_NUMBER LEFT OUTER JOIN ( SELECT C_FILE_ROUTE, C_FILE_UUID, C_FILE_NAME, COUNSELOR_NUMBER FROM ( SELECT C_FILE_ROUTE, C_FILE_UUID, C_FILE_NAME, COUNSELOR_NUMBER, RANK() OVER(PARTITION BY COUNSELOR_NUMBER ORDER BY C_FILE_NUMBER) RK FROM TBL_C_FILE ) WHERE RK = 1 ) J1 ON C.COUNSELOR_NUMBER = J1.COUNSELOR_NUMBER ORDER BY C.COUNSELOR_NUMBER DESC ) X
WHERE ROWNUM <= #{page}*#{amount}
) Y WHERE RNUM > (#{page} - 1) * #{amount}
