mapper.update:
UPDATE TBL_COUNSELOR
SET COUNSELOR_TITLE = #{counselorTitle}, COUNSELOR_CONTENT = #{counselorContent}
WHERE COUNSELOR_NUMBER = #{counselorNumber}

mapper.select:
SELECT RNUM, COUNSELOR_NUMBER, USER_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, USER_NAME FROM ( SELECT ROWNUM AS RNUM, COUNSELOR_NUMBER, USER_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, USER_NAME FROM( SELECT COUNSELOR_NUMBER, C.USER_NUMBER, COUNSELOR_TITLE, COUNSELOR_CONTENT, COUNSELOR_DATE, U.USER_NAME FROM TBL_COUNSELOR C JOIN TBL_USER U ON C.USER_NUMBER = U.USER_NUMBER ORDER BY COUNSELOR_NUMBER DESC ) X
WHERE ROWNUM <= #{page} * #{amount}
) Y WHERE RNUM > (#{page} - 1) * #{amount}
