mapper.update:
UPDATE TBL_PF_COMMENT
SET PF_COMMENT_CONTENT = #{replyContent}, PF_COMMENT_DATE = SYSDATE
WHERE PF_COMMENT_NUMBER = #{pfCommentNumber}

mapper.select:
SELECT PF_COMMENT_NUMBER, PF_COMMENT_CONTENT, PF_COMMENT_DATE, PROFILE_NUMBER, P.USER_NUMBER, USER_NAME FROM ( SELECT ROWNUM RNUM, PF_COMMENT_NUMBER, PF_COMMENT_CONTENT, PF_COMMENT_DATE, PROFILE_NUMBER, P.USER_NUMBER, USER_NAME FROM ( SELECT PF_COMMENT_NUMBER, PF_COMMENT_CONTENT, PF_COMMENT_DATE, PROFILE_NUMBER, P.USER_NUMBER, USER_NAME FROM TBL_PF_COMMENT P JOIN TBL_USER U ON PROFILE_NUMBER = #{profileNumber} AND P.USER_NUMBER = U.USER_NUMBER ORDER BY PF_COMMENT_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
